mapper.select:
		select title, category, nickname, board_id, write_date, view_cnt
		from board b
		join users u on u.username = b.username
		
		deleted_dt is null
			
       			and (content like concat('%', #{syntax}, '%') or title like concat('%', #{syntax}, '%'))
    		
   			
        		and nickname = #{nickname}
    		
    		
        		and category = #{category}
    		
		
		order by board_id desc
		limit #{startPage}, #{pageSize}

mapper.update:
update board 
set title=#{title},
category=#{category},
content=#{content}
where board_id=#{board_id}

mapper.select:
select count(board_id) from board b join users u on u.username=b.username
deleted_dt is null
and (content like concat('%', #{syntax}, '%') or title like concat('%', #{syntax}, '%'))
and nickname = #{nickname}
and category = #{category}

mapper.update:
update board
set deleted_dt = now()
where board_id=#{board_id}
